package Sec_6;

public class for_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i=1; i<=10; i++) {
			System.out.println("The value of i is: " + i);
		}
		
		for (int i=20; i>=10; i--) {
			System.out.println("The value of i is: " + i);
		}
	}

}
